SELECT settle_amt/100 from tb_saobei_settle_info where settle_date=DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 1 DAY),'%Y-%m-%d') and settle_card_no='740671***9001' order by settle_amt;SELECT * from tb_saobei_settle_info where merchant_no='850205812000485' and settle_date='2025-03-19';-- A208 乐刷SELECT settle_card_no,sum(settle_amt) from tb_saobei_settle_info where settle_date=DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 1 DAY),'%Y-%m-%d') and settle_card_no='740671***9001' group by settle_card_no;SELECT sum(settle_amount)/100 from tb_settlement_billing_deviation_record where bill_date='2024-08-13' and deleted=0;SELECT * from tb_settlement_billing_deviation_record where bill_date='2024-11-11' and deleted=0 and merchant_num='846605812000442';select * from tb_trans_record where id=6551;select * from tb_trans_detail where trans_date='2025-06-03' group by channel;select * from tb_trans_detail where channel=3 ORDER BY trans_date DESC LIMIT 100;select * from tb_trans_detail where channel!=1 and trans_record_id is null order by create_time desc limit 100;select * from tb_trans_record where id = 6690;select sum(settle_amt) from tb_saobei_settle_info t where t.settle_date='2025-04-14' and t.deleted=0 and t.settle_card_name='四川书亦餐饮管理有限公司';select * from tb_trans_record where trans_date='2025-01-07';-- 资金归集with bill_temp as (select distinct t1.store_code from(select store_code from tb_allocate_bill_app where bill_date=DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 2 DAY),'%Y-%m-%d')union allselect store_code from tb_allocate_bill_terminal where bill_date=DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 2 DAY),'%Y-%m-%d')) as t1)SELECT merchant_store_code,       merchant_num,settle_amount/100,       (app_amount+(settle_amount-IFNULL(app_amount,0)-IFNULL(hll_amount,0)-IFNULL(hs_amount,0)))/100 as '补app',       hs_amount/100 as '补HS'from tb_settlement_billing_deviation_record twhere bill_date=DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 2 DAY),'%Y-%m-%d') and deleted=0and merchant_store_code is not null and merchant_store_code!=''and not exists(select 1 from bill_temp where store_code=t.merchant_store_code) order by settle_amount/100;-- 找差异with bill_temp as (    select store_code,sum(bill_amount) as bill_amount from tb_store_bill where bill_date=DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 2 DAY),'%Y-%m-%d') and bill_channel in('1','2-1') group by store_code)SELECT t.id,t2.store_code,t.merchant_no,settle_date,settle_amt/100from tb_saobei_settle_info t left join tb_store_config t2 on t.merchant_no=t2.store_merchant_numwhere t.settle_date=DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 1 DAY),'%Y-%m-%d') and t.deleted=0 and t.is_settle_company =1 and t.settle_status =1  and not exists(select 1 from bill_temp where store_code=t2.store_code and bill_amount*100=t.settle_amt)order by settle_amt;-- 确定金额SELECT    d.trade_number,    case        when d.terminal_no = tsc.applet_terminal_num then 'app'        when d.terminal_no = tsc.pos_hs_terminal_num then 'pos'    else 'unknown' end as termial,    tsc.store_code ,    sum((d.transaction_received_amount - d.transaction_commission + d.refund_amount - d.refund_commission + d.other_transaction_received_amount - d.other_transaction_commission + d.other_refund_amount - d.other_refund_commission)/100) AS amountFROM    tb_bill_pos_applet_detail d inner join tb_store_config tsc on d.trade_number =tsc.store_merchant_numWHERE d.brand_id = 1000 AND d.bill_day = '2025-04-14'and d.trade_number IN('865105812003257')GROUP BY d.trade_number,d.terminal_noorder by termial ;-- 口碑--  确定总金额 date_format(settlement_time,'%Y-%m-%d'),sum(merchant_receipts-service_fee)select date_format(settlement_time,'%Y-%m-%d'),sum(merchant_receipts-service_fee) from tb_bill_kb_detailwhere settlement_time>=DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 3 DAY),'%Y-%m-%d') and settlement_time<DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 2 DAY),'%Y-%m-%d');-- 找出未写账单的数据   shop_id,sum(merchant_receipts-service_fee)with bill_temp as (    select shop_id,sum(merchant_receipts-service_fee) as bill_amount from tb_bill_kb_detail where settlement_time>=DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 3 DAY),'%Y-%m-%d') and settlement_time<DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 2 DAY),'%Y-%m-%d') group by shop_id)select shop_id,bill_amount from bill_temp t1 where not exists(    select 1 from tb_allocate_bill_kb t2 where t2.platform_code=t1.shop_id and t2.bill_amount=t1.bill_amount and t2.bill_date=DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 3 DAY),'%Y-%m-%d')) group by shop_id;select use_shop from tb_bill_kb_detail where shop_id = '76629026' and settlement_time>=DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 3 DAY),'%Y-%m-%d') and settlement_time<DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 2 DAY),'%Y-%m-%d');select * from tb_allocate_dispute_bill where channel=10 and available_amount>0 order by create_time desc limit 100;-- 支付宝团购-- 确定金额select sum(settlement_amt) from tb_bill_zfbtg_detail where settlement_time > '2025-06-16' and settlement_time < '2025-06-17' order by create_time desc ;SELECT sum(settlement_amt+payment_handling_fee+technical_handling_fee) from tb_bill_zfbtg_detail where settlement_time > '2025-06-16' and settlement_time < '2025-06-17';-- 找出未写账单的数据with bill_temp as (    SELECT zfb_id,sum(settlement_amt+payment_handling_fee+technical_handling_fee) bill_amount from tb_bill_zfbtg_detail where settlement_time > '2025-06-16' and settlement_time < '2025-06-17' group by zfb_id)select t2.zfb_id,t1.store_code,t1.bill_amount,t2.bill_amount from bill_temp t2 left join tb_allocate_bill_zfbtg t1 on t1.platform_code=t2.zfb_id and t1.bill_date='2025-06-14' where t1.bill_amount is null;select status,sum(bill_amount) from tb_allocate_bill_elm where create_time>'2024-07-27' and bill_date='2024-07-23' group by status;-- 美团外卖SELECT * from tb_trans_detail where trans_date='2025-03-20' and brand_id=1000 and channel=3 order by trans_amt;select * from tb_mt_takeout_settle_info t1 where store_code is null and settle_date=DATE_FORMAT(NOW(),'%Y-%m-%d')and exists(select 1 from tb_bill_mt_takeout_detail where settlement_setting_id='105798784'and mt_num=t1.mt_num and settlement_period=DATE_FORMAT(NOW(),'%Y-%m-%d')) order by  business_desc;-- 饿了么with bill_temp as (    select settle_shop_id,sum(settle_amt) as settle_amt from(        select settle_shop_id,-sum(settle_amt) as settle_amt from tb_elm_settle_info where trade_type='支出' and deleted=0 and settle_date=date_format(now(),'%Y-%m-%d') group by settle_shop_id        union all        select settle_shop_id,sum(settle_amt) as settle_amt from tb_elm_settle_info where trade_type='收入' and deleted=0 and settle_date=date_format(now(),'%Y-%m-%d') group by settle_shop_id    )T group by settle_shop_id)SELECT *from bill_temp t where not exists(select 1 from tb_allocate_bill_elm t1where t1.platform_code=t.settle_shop_id and t1.bill_date=DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 3 DAY),'%Y-%m-%d')and t1.bill_amount=t.settle_amt);-- 美团团购select * from tb_mt_tg_settle_info t1 where settle_date='2025-03-27' and not exists(    select 1 from tb_allocate_bill_mttg t2 where t2.bill_date='2025-03-26' and t1.mt_num=t2.platform_code    and t1.settle_amt=t2.bill_amount) and bill_date='2025-03-26';select  sum(settle_amt) from tb_mt_tg_settle_info t1 where settle_date='2025-02-06' and store_code is null;-- 快手select store_num,settle_amt from tb_ks_settle_info where settle_date='2025-04-02' and settle_status='已结算' and store_num not in(    select platform_code from tb_allocate_bill_ks where bill_date='2025-04-01');with bill_temp as (    select store_code,sum(bill_amount) as bill_amount from tb_store_bill where bill_date='2025-06-05' and bill_channel in('1','2-1') group by store_code)SELECT t.id,t2.store_code,t.merchant_no,settle_date,settle_amt/100from tb_saobei_settle_info t left join tb_store_config t2 on t.merchant_no=t2.store_merchant_numwhere t.settle_date='2025-06-06' and t.deleted=0 and t.is_settle_company =1 and t.settle_status =1  and not exists(select 1 from bill_temp where store_code=t2.store_code and bill_amount*100=t.settle_amt)order by settle_amt;